member.example:
Following code shows how to set the reader to items in ItemBindingExtender

public void ReadMyData(string myConnString) 
{
	string mySelectQuery = "SELECT OrderID, CustomerID FROM Orders";
	SqlConnection myConnection = new SqlConnection(myConnString);
	SqlCommand myCommand = new SqlCommand(mySelectQuery,myConnection);
	myConnection.Open();

	SqlDataReader myReader;
	myReader = myCommand.ExecuteReader();

	// Always call Read before accessing data.
	if (myReader.Read()) 
	{
		ItemBindingExtender1.SetItemValues(myReader);
	}

	// always call Close when done reading.
	myReader.Close();

	// Close the connection when done with it.
	myConnection.Close();
}


Public Sub ReadMyData(myConnString As String)
	Dim mySelectQuery As String = "SELECT OrderID, CustomerID FROM Orders"
	Dim myConnection As New SqlConnection(myConnString)
	Dim myCommand As New SqlCommand(mySelectQuery, myConnection)
	myConnection.Open()
	Dim myReader As SqlDataReader

	myReader = myCommand.ExecuteReader()

	' Always call Read before accessing data.
	If myReader.Read() Then
		ItemBindingExtender1.SetItemValues(myReader)
	End While

	' always call Close when done reading.
	myReader.Close()

	' Close the connection when done with it.
	myConnection.Close()
End Sub 'ReadMyData

Following code shows how to set the DataRow to items in ItemBindingExtender

private void CreateNewDataRow()
{
	// Use the MakeTable function below to create a new table.
	DataTable myTable;
	myTable = MakeNamesTable();

	// Once a table has been created, use the NewRow to create a DataRow.
	DataRow myRow;
	myRow = myTable.NewRow();

	// Then add the new row to the collection.
	myRow["fName"] = "John";
	myRow["lName"] = "Smith";

	// add row to some table
	myTable.Rows.Add(myRow);

	// set values to extender items
	ItemBindingExtender1.SetItemValues(myRow);
}


Private Sub CreateNewDataRow()
	' Use the MakeTable function below to create a new table.
	Dim myTable As DataTable
	myTable = MakeNamesTable()

	' Once a table has been created, use the NewRow to create a DataRow.
	Dim myRow As DataRow 
	myRow = myTable.NewRow()

	' Then add the new row to the collection.
	myRow("fName") = "John"
	myRow("lName") = "Smith"

	' add row to some table
	myTable.Rows.Add(myRow)

	'set values to extender items
	ItemBindingExtender1.SetItemValues(myRow)
End Sub

Following code shows how to set the Business Object to items in ItemBindingExtender

public class Customer
{
	private string name;
	private string address;

	public Customer(string n, string a)
	{
		name = n;
		address = a;
	}

	public string Name { get { return name; } }
	public string Address { get { return address; } }
}

// create object
Customer cst = new Customer("James Grog", "PureComponents");

// set values to extender items
ItemBindingExtender1.SetItemValues(cst);		


Public Class Customer
	Dim name As String
	Dim address As String

	Public Sub New(ByVal n As Atring, ByVal a As String)
		name = n
		address = a
	End Sub

	Public Property Name() As String
	Get 
		Return name
	End Get
	End Property

	Public Property Address() As String
	Get
		Return address
	End Get
	End Property
End Class

'create object
Dim cst As New Customer("James Grog", "PureComponents")

'set values to extender items
ItemBindingExtender1.SetItemValues(cst)

Following code shows how to set the Hashtable to items in ItemBindingExtender

private void SetHashtable()
{
	// Creates and initializes a new Hashtable.
	Hashtable myHT = new Hashtable();
	myHT.Add("First", "Hello");
	myHT.Add("Second", "World");
	myHT.Add("Third", "!");

	// set values to extender items
	ItemBindingExtender1.SetItemValues(myHT);
}


Private Sub SetHashtable()
	Dim myHT As New Hashtable()
	myHT.Add("First", "Hello")
	myHT.Add("Second", "World")
	myHT.Add("Third", "!")

	'set values to extender items
	ItemBindingExtender1.SetItemValues(myHT)
End Sub

